import React, { useState, useRef } from 'react';
import axios from 'axios';
import { Upload, Sparkles, Mail, Edit, FileText, Send, CheckCircle, AlertCircle, Loader2 } from 'lucide-react';
import { Button } from './components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './components/ui/card';
import { Textarea } from './components/ui/textarea';
import { Input } from './components/ui/input';
import { Label } from './components/ui/label';
import { Alert, AlertDescription } from './components/ui/alert';
import { Badge } from './components/ui/badge';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_BACKEND_URL;

function App() {
  const [transcript, setTranscript] = useState('');
  const [customPrompt, setCustomPrompt] = useState('');
  const [summary, setSummary] = useState('');
  const [summaryId, setSummaryId] = useState('');
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState({ show: false, type: '', message: '' });
  const [recipientEmails, setRecipientEmails] = useState('');
  const [editMode, setEditMode] = useState(false);
  const fileInputRef = useRef(null);

  const showAlert = (type, message) => {
    setAlert({ show: true, type, message });
    setTimeout(() => setAlert({ show: false, type: '', message: '' }), 5000);
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (!file.name.endsWith('.txt')) {
      showAlert('error', 'Please upload a .txt file only');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(`${API_BASE_URL}/api/upload-transcript`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success) {
        setTranscript(response.data.transcript);
        setFileName(file.name);
        showAlert('success', 'Transcript uploaded successfully!');
      }
    } catch (error) {
      console.error('Upload error:', error);
      showAlert('error', error.response?.data?.detail || 'Failed to upload transcript');
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateSummary = async () => {
    if (!transcript.trim()) {
      showAlert('error', 'Please upload a transcript first');
      return;
    }

    if (!customPrompt.trim()) {
      showAlert('error', 'Please enter a custom instruction');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE_URL}/api/generate-summary`, {
        transcript,
        custom_prompt: customPrompt,
      });

      if (response.data.success) {
        setSummary(response.data.summary);
        setSummaryId(response.data.summary_id);
        showAlert('success', 'Summary generated successfully!');
      }
    } catch (error) {
      console.error('Summary generation error:', error);
      showAlert('error', error.response?.data?.detail || 'Failed to generate summary');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateSummary = async () => {
    if (!summaryId || !summary.trim()) return;

    setLoading(true);
    try {
      const response = await axios.put(`${API_BASE_URL}/api/update-summary`, {
        summary_id: summaryId,
        updated_summary: summary,
      });

      if (response.data.success) {
        showAlert('success', 'Summary updated successfully!');
        setEditMode(false);
      }
    } catch (error) {
      console.error('Update error:', error);
      showAlert('error', error.response?.data?.detail || 'Failed to update summary');
    } finally {
      setLoading(false);
    }
  };

  const handleSendEmail = () => {
    if (!summary.trim()) {
      showAlert('error', 'No summary to send');
      return;
    }

    const subject = encodeURIComponent('Meeting Summary');
    const body = encodeURIComponent(`Hi,\n\nPlease find the meeting summary below:\n\n${summary}\n\nBest regards`);
    const recipients = recipientEmails ? `?subject=${subject}&body=${body}` : `?subject=${subject}&body=${body}`;

    window.location.href = `mailto:${recipientEmails}${recipients}`;
    showAlert('success', 'Email client opened with summary');
  };

  const promptExamples = [
    "Summarize in bullet points for executives",
    "Highlight only action items and deadlines",
    "Create a brief overview with key decisions made",
    "Extract main discussion points and next steps"
  ];

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-50 via-blue-50 to-indigo-50">
      <div className="container mx-auto px-4 py-8 max-w-6xl">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="flex items-center justify-center gap-3 mb-4">
            <div className="p-3 bg-gradient-to-r from-blue-500 to-indigo-600 rounded-2xl shadow-lg">
              <Sparkles className="h-8 w-8 text-white" />
            </div>
            <h1 className="text-4xl font-bold bg-gradient-to-r from-blue-600 to-indigo-600 bg-clip-text text-transparent">
              AI Meeting Notes Summarizer
            </h1>
          </div>
          <p className="text-xl text-slate-600 max-w-2xl mx-auto leading-relaxed">
            Transform your meeting transcripts into actionable summaries with AI-powered intelligence
          </p>
        </div>

        {/* Alert */}
        {alert.show && (
          <Alert className={`mb-6 ${alert.type === 'error' ? 'border-red-200 bg-red-50' : 'border-green-200 bg-green-50'}`}>
            {alert.type === 'error' ? (
              <AlertCircle className="h-4 w-4 text-red-600" />
            ) : (
              <CheckCircle className="h-4 w-4 text-green-600" />
            )}
            <AlertDescription className={alert.type === 'error' ? 'text-red-800' : 'text-green-800'}>
              {alert.message}
            </AlertDescription>
          </Alert>
        )}

        <div className="grid lg:grid-cols-2 gap-8">
          {/* Left Column - Upload & Input */}
          <div className="space-y-6">
            {/* File Upload */}
            <Card className="border-0 shadow-xl bg-white/80 backdrop-blur-sm">
              <CardHeader className="pb-4">
                <CardTitle className="flex items-center gap-2 text-slate-800">
                  <Upload className="h-5 w-5 text-blue-600" />
                  Upload Transcript
                </CardTitle>
                <CardDescription>
                  Upload your meeting transcript (.txt file)
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div 
                  className="border-2 border-dashed border-slate-300 rounded-xl p-8 text-center hover:border-blue-400 transition-colors cursor-pointer bg-slate-50/50"
                  onClick={() => fileInputRef.current?.click()}
                >
                  <FileText className="h-12 w-12 text-slate-400 mx-auto mb-4" />
                  <p className="text-slate-600 mb-2">
                    {fileName ? `Selected: ${fileName}` : 'Click to upload transcript file'}
                  </p>
                  <p className="text-sm text-slate-500">Supports .txt files only</p>
                </div>
                <input
                  ref={fileInputRef}
                  type="file"
                  accept=".txt"
                  onChange={handleFileUpload}
                  className="hidden"
                />
                {transcript && (
                  <div className="mt-4">
                    <Badge variant="secondary" className="bg-green-100 text-green-800">
                      <CheckCircle className="h-3 w-3 mr-1" />
                      Transcript loaded ({transcript.length} characters)
                    </Badge>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Custom Prompt */}
            <Card className="border-0 shadow-xl bg-white/80 backdrop-blur-sm">
              <CardHeader className="pb-4">
                <CardTitle className="flex items-center gap-2 text-slate-800">
                  <Edit className="h-5 w-5 text-blue-600" />
                  Custom Instructions
                </CardTitle>
                <CardDescription>
                  Tell the AI how you want your summary formatted
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-4">
                <div>
                  <Label htmlFor="prompt" className="text-sm font-medium text-slate-700">
                    Your Instructions
                  </Label>
                  <Textarea
                    id="prompt"
                    placeholder="e.g., Summarize in bullet points for executives..."
                    value={customPrompt}
                    onChange={(e) => setCustomPrompt(e.target.value)}
                    className="mt-2 min-h-[120px] border-slate-200 focus:border-blue-400 focus:ring-blue-400"
                  />
                </div>

                {/* Quick Examples */}
                <div>
                  <Label className="text-sm font-medium text-slate-700 mb-2 block">
                    Quick Examples:
                  </Label>
                  <div className="grid grid-cols-1 gap-2">
                    {promptExamples.map((example, index) => (
                      <button
                        key={index}
                        onClick={() => setCustomPrompt(example)}
                        className="text-left text-sm p-3 rounded-lg border border-slate-200 hover:border-blue-300 hover:bg-blue-50 transition-colors text-slate-600"
                      >
                        {example}
                      </button>
                    ))}
                  </div>
                </div>

                <Button 
                  onClick={handleGenerateSummary}
                  disabled={loading || !transcript || !customPrompt}
                  className="w-full bg-gradient-to-r from-blue-500 to-indigo-600 hover:from-blue-600 hover:to-indigo-700 text-white font-medium py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-200"
                >
                  {loading ? (
                    <>
                      <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                      Generating Summary...
                    </>
                  ) : (
                    <>
                      <Sparkles className="h-4 w-4 mr-2" />
                      Generate AI Summary
                    </>
                  )}
                </Button>
              </CardContent>
            </Card>
          </div>

          {/* Right Column - Summary & Share */}
          <div className="space-y-6">
            {/* Generated Summary */}
            <Card className="border-0 shadow-xl bg-white/80 backdrop-blur-sm">
              <CardHeader className="pb-4">
                <div className="flex items-center justify-between">
                  <CardTitle className="flex items-center gap-2 text-slate-800">
                    <FileText className="h-5 w-5 text-blue-600" />
                    AI Generated Summary
                  </CardTitle>
                  {summary && (
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => setEditMode(!editMode)}
                      className="border-blue-200 text-blue-600 hover:bg-blue-50"
                    >
                      <Edit className="h-4 w-4 mr-1" />
                      {editMode ? 'Save' : 'Edit'}
                    </Button>
                  )}
                </div>
                <CardDescription>
                  Your AI-generated meeting summary
                </CardDescription>
              </CardHeader>
              <CardContent>
                {summary ? (
                  <div className="space-y-4">
                    <Textarea
                      value={summary}
                      onChange={(e) => setSummary(e.target.value)}
                      readOnly={!editMode}
                      className={`min-h-[300px] text-sm leading-relaxed ${
                        editMode 
                          ? 'border-blue-200 focus:border-blue-400' 
                          : 'border-slate-200 bg-slate-50 cursor-default'
                      }`}
                    />
                    {editMode && (
                      <Button
                        onClick={handleUpdateSummary}
                        disabled={loading}
                        className="bg-green-600 hover:bg-green-700 text-white"
                      >
                        {loading ? (
                          <>
                            <Loader2 className="h-4 w-4 mr-2 animate-spin" />
                            Saving...
                          </>
                        ) : (
                          <>
                            <CheckCircle className="h-4 w-4 mr-2" />
                            Save Changes
                          </>
                        )}
                      </Button>
                    )}
                  </div>
                ) : (
                  <div className="text-center py-12 text-slate-500">
                    <Sparkles className="h-12 w-12 mx-auto mb-4 text-slate-300" />
                    <p>Your AI summary will appear here</p>
                    <p className="text-sm">Upload a transcript and generate a summary to get started</p>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Email Sharing */}
            {summary && (
              <Card className="border-0 shadow-xl bg-white/80 backdrop-blur-sm">
                <CardHeader className="pb-4">
                  <CardTitle className="flex items-center gap-2 text-slate-800">
                    <Mail className="h-5 w-5 text-blue-600" />
                    Share Summary
                  </CardTitle>
                  <CardDescription>
                    Send the summary via email
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <Label htmlFor="emails" className="text-sm font-medium text-slate-700">
                      Recipient Email(s) (optional)
                    </Label>
                    <Input
                      id="emails"
                      type="email"
                      placeholder="email@example.com, email2@example.com"
                      value={recipientEmails}
                      onChange={(e) => setRecipientEmails(e.target.value)}
                      className="mt-2 border-slate-200 focus:border-blue-400"
                    />
                    <p className="text-xs text-slate-500 mt-1">
                      Leave empty to open email client without recipients
                    </p>
                  </div>

                  <Button
                    onClick={handleSendEmail}
                    className="w-full bg-gradient-to-r from-green-500 to-emerald-600 hover:from-green-600 hover:to-emerald-700 text-white font-medium py-3 rounded-xl shadow-lg hover:shadow-xl transition-all duration-200"
                  >
                    <Send className="h-4 w-4 mr-2" />
                    Send via Email
                  </Button>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
