import React, { useState, useRef } from 'react';
import axios from 'axios';
import { Upload, Sparkles, Mail, Edit, FileText, Send, CheckCircle, AlertCircle, Loader2, Copy, X } from 'lucide-react';
import { Button } from './components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from './components/ui/card';
import { Textarea } from './components/ui/textarea';
import { Input } from './components/ui/input';
import { Label } from './components/ui/label';
import { Alert, AlertDescription } from './components/ui/alert';
import { Badge } from './components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from './components/ui/tabs';
import './App.css';

const API_BASE_URL = process.env.REACT_APP_BACKEND_URL;

function App() {
  const [transcript, setTranscript] = useState('');
  const [customPrompt, setCustomPrompt] = useState('');
  const [summary, setSummary] = useState('');
  const [summaryId, setSummaryId] = useState('');
  const [fileName, setFileName] = useState('');
  const [loading, setLoading] = useState(false);
  const [alert, setAlert] = useState({ show: false, type: '', message: '' });
  const [recipientEmails, setRecipientEmails] = useState('');
  const [editMode, setEditMode] = useState(false);
  const [inputMethod, setInputMethod] = useState('paste'); // 'paste' or 'upload'
  const fileInputRef = useRef(null);

  const showAlert = (type, message) => {
    setAlert({ show: true, type, message });
    setTimeout(() => setAlert({ show: false, type: '', message: '' }), 4000);
  };

  const handleFileUpload = async (event) => {
    const file = event.target.files[0];
    if (!file) return;

    if (!file.name.endsWith('.txt')) {
      showAlert('error', 'Please upload a .txt file only');
      return;
    }

    setLoading(true);
    try {
      const formData = new FormData();
      formData.append('file', file);

      const response = await axios.post(`${API_BASE_URL}/api/upload-transcript`, formData, {
        headers: {
          'Content-Type': 'multipart/form-data',
        },
      });

      if (response.data.success) {
        setTranscript(response.data.transcript);
        setFileName(file.name);
        showAlert('success', 'Transcript uploaded successfully!');
      }
    } catch (error) {
      console.error('Upload error:', error);
      showAlert('error', error.response?.data?.detail || 'Failed to upload transcript');
    } finally {
      setLoading(false);
    }
  };

  const handleGenerateSummary = async () => {
    if (!transcript.trim()) {
      showAlert('error', 'Please provide a transcript first');
      return;
    }

    if (!customPrompt.trim()) {
      showAlert('error', 'Please enter a custom instruction');
      return;
    }

    setLoading(true);
    try {
      const response = await axios.post(`${API_BASE_URL}/api/generate-summary`, {
        transcript,
        custom_prompt: customPrompt,
      });

      if (response.data.success) {
        setSummary(response.data.summary);
        setSummaryId(response.data.summary_id);
        setEditMode(true); // Enable edit mode by default
        showAlert('success', 'Summary generated successfully!');
      }
    } catch (error) {
      console.error('Summary generation error:', error);
      showAlert('error', error.response?.data?.detail || 'Failed to generate summary');
    } finally {
      setLoading(false);
    }
  };

  const handleUpdateSummary = async () => {
    if (!summaryId || !summary.trim()) return;

    setLoading(true);
    try {
      const response = await axios.put(`${API_BASE_URL}/api/update-summary`, {
        summary_id: summaryId,
        updated_summary: summary,
      });

      if (response.data.success) {
        showAlert('success', 'Summary updated successfully!');
        setEditMode(false);
      }
    } catch (error) {
      console.error('Update error:', error);
      showAlert('error', error.response?.data?.detail || 'Failed to update summary');
    } finally {
      setLoading(false);
    }
  };

  const handleSendEmail = () => {
    if (!summary.trim()) {
      showAlert('error', 'No summary to send');
      return;
    }

    if (editMode) {
      showAlert('error', 'Please save your edits before sending');
      return;
    }

    const subject = encodeURIComponent('Meeting Summary');
    const body = encodeURIComponent(`Hi,\n\nPlease find the meeting summary below:\n\n${summary}\n\nBest regards`);
    const recipients = recipientEmails ? `?subject=${subject}&body=${body}` : `?subject=${subject}&body=${body}`;

    window.location.href = `mailto:${recipientEmails}${recipients}`;
    showAlert('success', 'Email client opened with summary');
  };

  const clearAll = () => {
    setTranscript('');
    setSummary('');
    setCustomPrompt('');
    setFileName('');
    setSummaryId('');
    setEditMode(false);
    setRecipientEmails('');
  };

  const promptExamples = [
    "Summarize in bullet points for executives",
    "Extract key action items and deadlines",
    "Create executive summary with decisions",
    "List main discussion points and next steps"
  ];

  return (
    <div className="min-h-screen bg-gray-950 text-gray-100">
      <div className="container mx-auto px-6 py-8 max-w-7xl">
        {/* Header */}
        <div className="text-center mb-12">
          <div className="flex items-center justify-center gap-4 mb-6">
            <div className="p-4 bg-gradient-to-br from-blue-500 to-purple-600 rounded-xl shadow-2xl">
              <Sparkles className="h-8 w-8 text-white" />
            </div>
            <h1 className="text-5xl font-bold bg-gradient-to-r from-blue-400 via-purple-400 to-cyan-400 bg-clip-text text-transparent">
              AI Notes Summarizer
            </h1>
          </div>
          <p className="text-xl text-gray-400 max-w-2xl mx-auto">
            Transform your meeting notes into actionable summaries with AI
          </p>
        </div>

        {/* Alert */}
        {alert.show && (
          <Alert className={`mb-8 border-0 ${
            alert.type === 'error' 
              ? 'bg-red-950/50 text-red-200 border-red-800/30' 
              : 'bg-emerald-950/50 text-emerald-200 border-emerald-800/30'
          }`}>
            {alert.type === 'error' ? (
              <AlertCircle className="h-5 w-5" />
            ) : (
              <CheckCircle className="h-5 w-5" />
            )}
            <AlertDescription className="text-base font-medium">
              {alert.message}
            </AlertDescription>
          </Alert>
        )}

        <div className="grid lg:grid-cols-2 gap-8">
          {/* Left Column - Input */}
          <div className="space-y-6">
            {/* Input Method Toggle */}
            <Card className="bg-gray-900/50 border-gray-800 backdrop-blur-sm">
              <CardHeader className="pb-4">
                <CardTitle className="text-gray-100 text-xl">Input Method</CardTitle>
                <CardDescription className="text-gray-400">
                  Choose how to provide your meeting notes
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Tabs value={inputMethod} onValueChange={setInputMethod} className="w-full">
                  <TabsList className="grid w-full grid-cols-2 bg-gray-800 border-gray-700">
                    <TabsTrigger value="paste" className="data-[state=active]:bg-blue-600 data-[state=active]:text-white">
                      <Copy className="h-4 w-4 mr-2" />
                      Paste Text
                    </TabsTrigger>
                    <TabsTrigger value="upload" className="data-[state=active]:bg-blue-600 data-[state=active]:text-white">
                      <Upload className="h-4 w-4 mr-2" />
                      Upload File
                    </TabsTrigger>
                  </TabsList>

                  <TabsContent value="paste" className="mt-6">
                    <div className="space-y-4">
                      <Label className="text-gray-300 text-sm font-medium">
                        Paste your meeting notes here
                      </Label>
                      <Textarea
                        placeholder="Paste your meeting transcript, notes, or any text you want to summarize..."
                        value={transcript}
                        onChange={(e) => setTranscript(e.target.value)}
                        className="min-h-[200px] bg-gray-800/50 border-gray-700 text-gray-100 placeholder-gray-500 focus:border-blue-500 focus:ring-blue-500/20 resize-none"
                      />
                      {transcript && (
                        <Badge variant="secondary" className="bg-emerald-900/30 text-emerald-300 border-emerald-800/50">
                          <CheckCircle className="h-3 w-3 mr-1" />
                          {transcript.length} characters
                        </Badge>
                      )}
                    </div>
                  </TabsContent>

                  <TabsContent value="upload" className="mt-6">
                    <div 
                      className="border-2 border-dashed border-gray-700 rounded-xl p-8 text-center hover:border-blue-500/50 transition-all cursor-pointer bg-gray-800/30 hover:bg-gray-800/50"
                      onClick={() => fileInputRef.current?.click()}
                    >
                      <FileText className="h-12 w-12 text-gray-500 mx-auto mb-4" />
                      <p className="text-gray-300 mb-2">
                        {fileName ? `Selected: ${fileName}` : 'Click to upload .txt file'}
                      </p>
                      <p className="text-sm text-gray-500">Supports .txt files only</p>
                    </div>
                    <input
                      ref={fileInputRef}
                      type="file"
                      accept=".txt"
                      onChange={handleFileUpload}
                      className="hidden"
                    />
                  </TabsContent>
                </Tabs>
              </CardContent>
            </Card>

            {/* Custom Prompt */}
            <Card className="bg-gray-900/50 border-gray-800 backdrop-blur-sm">
              <CardHeader className="pb-4">
                <CardTitle className="text-gray-100 text-xl">AI Instructions</CardTitle>
                <CardDescription className="text-gray-400">
                  Tell the AI how to summarize your content
                </CardDescription>
              </CardHeader>
              <CardContent className="space-y-6">
                <div>
                  <Label className="text-gray-300 text-sm font-medium mb-3 block">
                    Your Instructions
                  </Label>
                  <Textarea
                    placeholder="e.g., Create bullet points highlighting key decisions and action items..."
                    value={customPrompt}
                    onChange={(e) => setCustomPrompt(e.target.value)}
                    className="min-h-[100px] bg-gray-800/50 border-gray-700 text-gray-100 placeholder-gray-500 focus:border-blue-500 focus:ring-blue-500/20"
                  />
                </div>

                {/* Quick Examples */}
                <div>
                  <Label className="text-gray-300 text-sm font-medium mb-3 block">
                    Quick Templates:
                  </Label>
                  <div className="grid gap-2">
                    {promptExamples.map((example, index) => (
                      <button
                        key={index}
                        onClick={() => setCustomPrompt(example)}
                        className="text-left text-sm p-3 rounded-lg bg-gray-800/30 border border-gray-700/50 hover:border-blue-500/50 hover:bg-gray-800/50 transition-all text-gray-300 hover:text-gray-100"
                      >
                        {example}
                      </button>
                    ))}
                  </div>
                </div>

                <div className="flex gap-3">
                  <Button 
                    onClick={handleGenerateSummary}
                    disabled={loading || !transcript || !customPrompt}
                    className="flex-1 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-medium py-6 rounded-xl shadow-xl hover:shadow-2xl transition-all duration-200 disabled:opacity-50"
                  >
                    {loading ? (
                      <>
                        <Loader2 className="h-5 w-5 mr-2 animate-spin" />
                        Generating...
                      </>
                    ) : (
                      <>
                        <Sparkles className="h-5 w-5 mr-2" />
                        Generate Summary
                      </>
                    )}
                  </Button>

                  <Button 
                    onClick={clearAll}
                    variant="outline"
                    className="px-6 py-6 border-gray-700 text-gray-400 hover:bg-gray-800 hover:text-gray-200 hover:border-gray-600"
                  >
                    <X className="h-4 w-4" />
                  </Button>
                </div>
              </CardContent>
            </Card>
          </div>

          {/* Right Column - Summary & Actions */}
          <div className="space-y-6">
            {/* Generated Summary */}
            <Card className="bg-gray-900/50 border-gray-800 backdrop-blur-sm">
              <CardHeader className="pb-4">
                <div className="flex items-center justify-between">
                  <div>
                    <CardTitle className="text-gray-100 text-xl">AI Summary</CardTitle>
                    <CardDescription className="text-gray-400">
                      {editMode ? 'Edit your summary before sharing' : 'Your generated summary'}
                    </CardDescription>
                  </div>
                  {summary && (
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => editMode ? handleUpdateSummary() : setEditMode(true)}
                      disabled={loading}
                      className="border-gray-700 text-gray-300 hover:bg-gray-800 hover:text-gray-100"
                    >
                      {loading ? (
                        <Loader2 className="h-4 w-4 mr-1 animate-spin" />
                      ) : editMode ? (
                        <CheckCircle className="h-4 w-4 mr-1" />
                      ) : (
                        <Edit className="h-4 w-4 mr-1" />
                      )}
                      {editMode ? 'Save' : 'Edit'}
                    </Button>
                  )}
                </div>
              </CardHeader>
              <CardContent>
                {summary ? (
                  <div className="space-y-4">
                    <Textarea
                      value={summary}
                      onChange={(e) => setSummary(e.target.value)}
                      readOnly={!editMode}
                      className={`min-h-[300px] text-sm leading-relaxed ${
                        editMode 
                          ? 'bg-gray-800/50 border-gray-700 text-gray-100 focus:border-blue-500' 
                          : 'bg-gray-800/30 border-gray-700/50 text-gray-200 cursor-default'
                      }`}
                    />
                    {editMode && (
                      <div className="flex items-center gap-2 text-sm text-amber-400 bg-amber-950/30 p-3 rounded-lg border border-amber-800/30">
                        <AlertCircle className="h-4 w-4" />
                        Save your changes before sending the summary
                      </div>
                    )}
                  </div>
                ) : (
                  <div className="text-center py-16 text-gray-500">
                    <Sparkles className="h-16 w-16 mx-auto mb-6 text-gray-700" />
                    <p className="text-lg mb-2">Your AI summary will appear here</p>
                    <p className="text-sm">Provide content and generate a summary to get started</p>
                  </div>
                )}
              </CardContent>
            </Card>

            {/* Email Sharing */}
            {summary && (
              <Card className="bg-gray-900/50 border-gray-800 backdrop-blur-sm">
                <CardHeader className="pb-4">
                  <CardTitle className="text-gray-100 text-xl">Share Summary</CardTitle>
                  <CardDescription className="text-gray-400">
                    Send your summary via email
                  </CardDescription>
                </CardHeader>
                <CardContent className="space-y-4">
                  <div>
                    <Label className="text-gray-300 text-sm font-medium mb-2 block">
                      Recipient Email(s) (optional)
                    </Label>
                    <Input
                      type="email"
                      placeholder="recipient@example.com"
                      value={recipientEmails}
                      onChange={(e) => setRecipientEmails(e.target.value)}
                      className="bg-gray-800/50 border-gray-700 text-gray-100 placeholder-gray-500 focus:border-blue-500"
                    />
                    <p className="text-xs text-gray-500 mt-2">
                      Leave empty to open your email client without recipients
                    </p>
                  </div>

                  <Button
                    onClick={handleSendEmail}
                    disabled={editMode}
                    className={`w-full font-medium py-6 rounded-xl shadow-xl transition-all duration-200 ${
                      editMode 
                        ? 'bg-gray-700 text-gray-500 cursor-not-allowed' 
                        : 'bg-gradient-to-r from-emerald-600 to-teal-600 hover:from-emerald-700 hover:to-teal-700 text-white hover:shadow-2xl'
                    }`}
                  >
                    <Send className="h-5 w-5 mr-2" />
                    {editMode ? 'Save Changes First' : 'Send via Email'}
                  </Button>
                </CardContent>
              </Card>
            )}
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
